INSERT INTO `tbl_pages` (`page_id`, `page_title`, `page_url`, `content`, `created_date`, `creator`, `updated_at`, `updated_by`) VALUES
(1, 'Giới Thiệu', 'viet-son', '<p><strong>TH&Ocirc;NG B&Aacute;O CH&Iacute;NH THỨC TỪ C&Ocirc;NG TY CỔ PHẦN TIN HỌC VIẾT SƠN</strong>.</p>\r\n\r\n<p>Kể từ ng&agrave;y 25/07/2024, tập đo&agrave;n Achieva Technology SDN BHD ch&iacute;nh thức trở th&agrave;nh Nh&agrave; Ph&acirc;n Phối khu vực của Intel ( Intel Authorized Distributor) tại thị trường Việt Nam.</p>\r\n\r\n<p>Được sự ủy th&aacute;c v&agrave; t&iacute;n nhiệm từ tập đo&agrave;n Achieva :</p>\r\n\r\n<p>C&ocirc;ng Ty Cổ Phần Tin Học Viết Sơn ch&iacute;nh thức trở th&agrave;nh đối t&aacute;c đại diện , với mục ti&ecirc;u th&uacute;c đẩy hoạt động kinh doanh v&agrave; ph&acirc;n phối sản phẩm Intel tại thị trường Việt Nam.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i cam kết sẽ lu&ocirc;n mang lại những gi&aacute; trị tốt nhất cho tất cả Qu&yacute; đối t&aacute;c kinh doanh cũng như Qu&yacute; Kh&aacute;ch H&agrave;ng đ&atilde; lựa chọn sản phẩm Intel v&agrave; C&ocirc;ng ty Viết Sơn</p>\r\n\r\n<p>--------------------------------------------------------</p>\r\n\r\n<p>VIẾT SƠN - Leading Distributor in the Internet of Every things</p>\r\n\r\n<p><img alt=\"????\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/tf6/1/16/1f3e0.png\" style=\"height:16px; width:16px\" />&nbsp;150Ter B&ugrave;i Thị Xu&acirc;n, P. Phạm Ngũ L&atilde;o, Q.1, TP.HCM</p>\r\n\r\n<p><img alt=\"????\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/tf6/1/16/1f3e0.png\" style=\"height:16px; width:16px\" />&nbsp;Chi nh&aacute;nh HN : tầng 4, số 1 Th&aacute;i H&agrave;- Trung Liệt, Đống Đa, H&agrave; Nội.</p>\r\n', 1667844272, 'Văn An', 1722054232, 'Văn Thị Hoài An'),
(2, 'Thông báo bảo hành', 'thong-bao-bao-hanh', '<p>K&iacute;nh gửi : Qu&yacute; Đại L&yacute;</p>\r\n\r\n<p>Nhằm n&acirc;ng cao chất lượng phục vụ, mang đến sự h&agrave;i l&ograve;ng đến Qu&yacute; Đại L&yacute; đ&atilde; v&agrave; đang ủng hộ C&ocirc;ng ty Cổ Phần Tin Học Viết Sơn trong suốt thời gian qua; Nay C&ocirc;ng ty ch&uacute;ng t&ocirc;i xin gởi đến Qu&yacute; Đại L&yacute; một số thay đổi về ch&iacute;nh s&aacute;ch bảo h&agrave;nh của C&ocirc;ng ty ch&uacute;ng t&ocirc;i như sau:</p>\r\n\r\n<ol>\r\n	<li><strong>Thời gian nhận v&agrave; trả bảo h&agrave;nh</strong>:</li>\r\n</ol>\r\n\r\n<p>Từ Thứ Hai đến Thứ Bảy</p>\r\n\r\n<p>S&aacute;ng từ 8:00 đến 12:00</p>\r\n\r\n<p>Chiều từ 13:30 đến 17:30</p>\r\n\r\n<ol>\r\n	<li value=\"2\"><strong>Đối tượng bảo h&agrave;nh:</strong>&nbsp;Tất cả Kh&aacute;ch h&agrave;ng mua b&aacute;n, sử dụng những sản phẩm c&oacute; tem nhận diện của C&ocirc;ng ty Viết Sơn được d&aacute;n tr&ecirc;n sản phẩm.</li>\r\n	<li value=\"3\"><strong>Chi ph&iacute; vận chuyển:</strong>&nbsp;Đối với Kh&aacute;ch h&agrave;ng ngo&agrave;i phạm vi H&agrave; Nội v&agrave; TP. Hồ Ch&iacute; Minh:</li>\r\n</ol>\r\n\r\n<p>Đối với kh&aacute;ch h&agrave;ng&nbsp;<strong>đầu cuối</strong>&nbsp;ở Tỉnh: Qu&yacute; kh&aacute;ch h&agrave;ng phải chịu ph&iacute; cả 02 đầu gửi đi v&agrave; nhận về.</p>\r\n\r\n<p>Đối với kh&aacute;ch h&agrave;ng l&agrave;&nbsp;<strong>Đại l&yacute;</strong>&nbsp;tại c&aacute;c Tỉnh: Đầu n&agrave;o gửi, đầu đ&oacute; thanh to&aacute;n (hỗ trợ 1 chiều).</p>\r\n\r\n<ol>\r\n	<li value=\"4\"><strong>Nơi tiếp nhận bảo h&agrave;nh</strong>:</li>\r\n</ol>\r\n\r\n<p><strong>Tại H&agrave; Nội:</strong>&nbsp;Chi nh&aacute;nh C&ocirc;ng ty Cổ Phần Tin Học Viết Sơn</p>\r\n\r\n<p>Tầng 4, T&ograve;a nh&agrave; Viet Tower, số 01 phố Th&aacute;i H&agrave;, P.Trung Liệt, Q. Đống Đa, TP H&agrave; Nội.</p>\r\n\r\n<p>Người li&ecirc;n hệ:&nbsp;<strong>Anh Chử Kim Vinh, số điện thoại 0936699336.</strong></p>\r\n\r\n<p><strong>Tại Tp. Hồ Ch&iacute; Minh:</strong>&nbsp;C&ocirc;ng ty Cổ Phần Tin Học Viết Sơn</p>\r\n\r\n<p>150 Ter, đường B&ugrave;i Thị Xu&acirc;n, phường Phạm Ngủ L&atilde;o, Quận 1, TP. Hồ Ch&iacute; Minh</p>\r\n\r\n<p>Người li&ecirc;n hệ:&nbsp;<strong>Chị Huỳnh Thị Vi&ecirc;n, số điện thoại 028 39260996.</strong></p>\r\n\r\n<p><strong>Đ&agrave; Nẵng</strong>: Ch&uacute;ng t&ocirc;i cam kết sẽ tiến h&agrave;nh mở TTBH trong thời gian sớm nhất c&oacute; thể.</p>\r\n\r\n<ol>\r\n	<li value=\"5\"><strong>Điều kiện bảo h&agrave;nh</strong>:</li>\r\n</ol>\r\n\r\n<p>Theo đ&uacute;ng ti&ecirc;u chuẩn của nh&agrave; sản xuất.</p>\r\n\r\n<p>Trong trường hợp sản phẩm kh&ocirc;ng đảm bảo c&aacute;c điều kiện bảo h&agrave;nh, C&ocirc;ng ty ch&uacute;ng t&ocirc;i sẵn s&agrave;ng hỗ trợ, sửa chữa hộ Qu&yacute; Đại l&yacute; nếu c&oacute; y&ecirc;u cầu.</p>\r\n\r\n<p><strong>Hotline:</strong></p>\r\n\r\n<p>Nhằm hỗ trợ tốt nhất đến kh&aacute;ch h&agrave;ng, C&ocirc;ng ty ch&uacute;ng t&ocirc;i đ&atilde; thiết lập c&aacute;c phương tiện ghi nhận c&aacute;c phản hồi về chất lượng bảo h&agrave;nh, rất mong nhận được g&oacute;p &yacute; ch&acirc;n th&agrave;nh của Qu&yacute; Đại l&yacute; qua c&aacute;c phương tiện truyền th&ocirc;ng như:</p>\r\n\r\n<p>Website: vietsontdc.com/hotro</p>\r\n\r\n<p>Fanpage:&nbsp;<a href=\"https://www.facebook.com/VietSonICT/\">https://www.facebook.com/VietSonICT/</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tất cả những g&oacute;p &yacute; của Qu&yacute; Đại l&yacute; đều được Ch&uacute;ng t&ocirc;i ghi nhận v&agrave; điều chỉnh, sửa chữa để dịch vụ bảo h&agrave;nh m&agrave; Ch&uacute;ng t&ocirc;i cung cấp đến Qu&yacute; kh&aacute;ch ng&agrave;y c&agrave;ng được n&acirc;ng cao</p>\r\n\r\n<p>Rất mong nhận được sự ủng hộ của Qu&yacute; Đại l&yacute;.<br />\r\nTr&acirc;n trọng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1667913957, 'Văn An', 1722054330, 'Văn Thị Hoài An');

